INSERT INTO `practices` (`id`, `title`, `content`) VALUES
(1, 'Задание 1 Вёрстка адаптивной секции с карточками', '<section class=\"lesson\">\n        <div class=\"container\">\n            <div class=\"row changeable-text\">\n                <h1 class=\"text-left \">Задание 1 Вёрстка адаптивной секции с карточками</h1>\n                <h2 class=\"col-12 col-lg-6\">Описание задания</h2>\n\n                <p class=\"changeable-text\">\n                    В первом практическом задании вам предстоит создать элемент страницы, содержащий карточки. Это очень популярный на сайтах вид отображения блока с информацией, особенно удобный для просмотра с мобильных устройств.\n                </p>\n                <p class=\"changeable-text\">\n                    Цель этого задания - научиться создавать адаптивные секции с использованием карточек в Bootstrap. Вам необходимо разработать простую секцию, состоящую из нескольких карточек, которые будут хорошо смотреться на различных устройствах и экранах.\n                </p>\n                <p class=\"changeable-text\">\n                    Описание задания: Ваша задача состоит в том, чтобы создать простую вёрстку адаптивной секции с карточками. В этой секции должно быть несколько карточек, каждая из которых содержит изображение, заголовок и описание. Карточки должны быть выровнены горизонтально и должны адаптироваться под разные размеры экрана. Секция должна быть полностью адаптивна до ширины 280px включительно.\n                </p>\n                <p class=\"changeable-text\">\n                    В этом задании от вас ожидается применение таких изученных классов фреймворка, как <code>.col</code> и <code>.card</code>.\n                    <br>\n                    Ниже представлен примерный вариант того, как должна выглядеть ваша секция.\n                </p>\n\n                <img class=\"col-11 col-md-5 lesson-img\" src=\"../images/practice/cards-example.png\" alt=\"download-link\">\n\n                <p class=\"changeable-text\">\n                    Рекомендуемое время на выполнение задания: 45 минут.\n                </p>\n\n\n\n                <div class=\"lesson-btns col-12 col-lg-8 m-auto\">\n                    <div class=\"row d-flex justify-content-between m-0\">\n                        <a href=\"2\" class=\"mt-4 col-12 \">\n                            <div class=\"w-100 hero-btn hero-btn-first d-flex align-items-center justify-content-center\">\n                                Следующее задание\n                            </div>\n                        </a>\n                    </div>\n                </div>\n\n            </div>\n        </div>\n    </section>'),
(2, 'Задание 2 Создание главной страницы', '<section class=\"lesson\">\n        <div class=\"container\">\n            <div class=\"row changeable-text\">\n                <h1 class=\"text-left \">Задание 2 Создание главной страницы</h1>\n                <h2 class=\"col-12 col-lg-6\">Описание задания</h2>\n\n                <p class=\"changeable-text\">\n                    Во втором практическом задании вам предстоит создать несложную главную страницу сайта, используя созданную на прошлом практическом задании секцию.\n                </p>\n                <p class=\"changeable-text\">\n                    Цель этого задания - научиться создавать более сложные структуры с использованием различных элементов Bootstrap.\n                </p>\n                <p class=\"changeable-text\">\n                    Описание задания: Вам необходимо разработать главную страницу сайта, состоящую из текста с кнопками слева и изображения справа. Помимо этого, в секцию <code>main</code> страницы, необходимо вставить уже созданные вами карточки, а так же создать подвал сайта со ссылками. Страница должна быть полностью адаптивна до ширины 280px включительно.\n                </p>\n                <p class=\"changeable-text\">\n                    Для размещения текста и кнопок слева вам необходимо использовать Bootstrap-сетку. Например, вы можете создать две колонки: одну для текста и кнопок, и другую для изображения. Вы также можете использовать классы Bootstrap для стилизации текста и кнопок. \n                    <br>\n                    Ниже представлен примерный вариант того, как должна выглядеть ваша страница.\n                </p>\n\n                <img class=\"col-11 col-md-5 lesson-img\" src=\"../images/practice/2nd-task-img.jpg\" alt=\"download-link\">\n\n                <p class=\"changeable-text\">\n                    Рекомендуемое время на выполнение задания: 1 час 30 минут.\n                </p>\n\n\n\n                <div class=\"lesson-btns col-12 col-lg-8 m-auto\">\n                    <div class=\"row d-flex justify-content-between m-0\">\n                        <a href=\"3\" class=\"mt-4 col-12 \">\n                            <div class=\"w-100 hero-btn hero-btn-first d-flex align-items-center justify-content-center\">\n                                Следующее задание\n                            </div>\n                        </a>\n                    </div>\n                </div>\n\n            </div>\n        </div>\n    </section>'),
(3, 'Задание 3 Создание более сложной страницы сайта', '<section class=\"lesson\">\n        <div class=\"container\">\n            <div class=\"row changeable-text\">\n                <h1 class=\"text-left \">Задание 3 Создание более сложной страницы сайта</h1>\n                <h2 class=\"col-12 col-lg-6\">Описание задания</h2>\n\n                <p class=\"changeable-text\">\n                    В третьем практическом задании вам предстоит создать более сложную страницу сайта.\n                </p>\n                <p class=\"changeable-text\">\n                    Цель этого задания - научиться создавать интерактивные страницы с использованием таких классов в bootstrap, как <code>carousel</code> и <code>accordion</code>.\n                </p>\n                <p class=\"changeable-text\">\n                    Описание задания: Ваша задача состоит в том, чтобы создать страницу, на которой будут размещены слайдер, аккордеоны и форма обратной связи. Так же на странице должна быть панель навигации. Вам необходимо разработать страницу, которая будет включать в себя эти элементы и обеспечивать удобный пользовательский опыт на различных устройствах. Страница должна быть полностью адаптивна до ширины 280px включительно, а панель навигации должна сворачиваться.\n                </p>\n                <p class=\"changeable-text\">\n                    Используйте все изученные в этом курсе навыки, для создания страницы. Прототип внешнего вида страницы вам предоставлен не будет. Необходимо создать страницу, опираясь на словесное описание.\n                </p>\n\n                <p class=\"changeable-text\">\n                    В шапке страницы должен быть логотип, и минимум 4 ссылки, а так же поле поиска и иконка личного кабинета. Шапка должна сворачиваться при ширине ниже 992 пикселя. Ниже шапки расположен слайдер, в котором автоматически листаются минимум 4 картинки. Под слайдером расположена секция, разделённая на две части. Левая часть занимает одну треть контейнера, и содержит в себе картинку, а правая часть занимает две трети контейнера, и содержит в себе заголовок и блок с текстом. Под этой секцией, расположена секция с заголовком \"часто задаваемые вопросы\", где представлены закрытые аккордеоны с вопросами, нажимая на которые, открывается текст с ответом на этот вопрос. Под этой секцией, находится форма обратной связи, состоящая из поля ввода email и элемента textarea, а так же кнопки \"отправить\", при нажатии на которую, появляется модальное окно с текстом \"сообщение отправлено\". В самом низу страницы располагается подвал со ссылками\n                </p>\n                <p class=\"changeable-text\">\n                    Рекомендуемое время на выполнение задания: 1 час 30 минут.\n                </p>\n\n            </div>\n        </div>\n    </section>');

